import { useEffect, useRef, useState } from "react";
import SockJS from 'sockjs-client';
import { CompatClient, Stomp } from "@stomp/stompjs";
import FindObject from "../classes/FindObject";

let stompClient: CompatClient;

export default function useStomp(connectionUrl: string) {
    const [message, setMessage] = useState('');

    useEffect(() => {
        const socket = new SockJS(connectionUrl);
        stompClient = Stomp.over(socket);
        stompClient.connect({}, function () {
            stompClient.subscribe('/results/snow', function (greeting) {
                setMessage(JSON.parse(greeting.body).city);
            });
        });
    }, [connectionUrl]);

    const send = () => {
        stompClient.send("/find/snow", {}, JSON.stringify(new FindObject('zip', 74467, 50)));
    };

    return {
        message,
        send
    }
}
