import { useEffect, useRef, useState } from "react";
import SockJS from 'sockjs-client';
import { CompatClient, Stomp, Client } from "@stomp/stompjs";
import FindObject from "../classes/FindObject";

let stompClient: CompatClient;

export default function useStomp(connectionUrl: string) {
    const [message, setMessage] = useState('');

    useEffect(() => {
        const socket = new SockJS(connectionUrl);
        const client = new Client({ brokerURL: connectionUrl });
        client.onConnect = () => {
            client.subscribe('/results/snow', function (greeting) {
                setMessage(JSON.parse(greeting.body).name);
            });
        };
    }, [connectionUrl]);

    const send = () => {
        stompClient.send("/find/snow", {}, JSON.stringify(new FindObject('zip', 74467, 50)));
    };

    return {
        message,
        send
    }
}
