import { useEffect, useState } from "react";
import SockJS from 'sockjs-client';
import { Stomp, CompatClient, Client } from "@stomp/stompjs";
import FindObject from "../classes/FindObject";

// let stompClient: CompatClient;
let stompClient: Client;

export default function useStomp(connectionUrl: string) {
    const [message, setMessage] = useState('');

    useEffect(() => {
        // connectionUrl = '/find-snow';
        // const socket = new SockJS(connectionUrl);
        // stompClient = Stomp.over(() => socket);
        stompClient = new Client({ 
            brokerURL: connectionUrl,
            reconnectDelay: 5000,
            heartbeatIncoming: 4000,
            heartbeatOutgoing: 4000,
        });
        stompClient.onConnect = () => {
            console.log("Connected!!");
            stompClient.subscribe('/results/snow', function (result) {
                setMessage(String(JSON.parse(result.body)));
            });
        };
        stompClient.onStompError = function (frame) {
            // Will be invoked in case of error encountered at Broker
            // Bad login/passcode typically will cause an error
            // Complaint brokers will set `message` header with a brief message. Body may contain details.
            // Compliant brokers will terminate the connection after any error
            console.log('Broker reported error: ' + frame.headers['message']);
            console.log('Additional details: ' + frame.body);
        };
        stompClient.activate();
    }, [connectionUrl]);

    const send = () => {
        stompClient.publish({ 
            destination: "/find/snowInUS", 
            body: JSON.stringify(new FindObject('zip', 74467, 50)),
            skipContentLengthHeader: true
        });
    };

    return {
        message,
        send
    }
}
