import React, { useEffect, useRef, useState } from 'react';
import logo from './logo.svg';
import './App.css';
import useStomp from './hooks/useStomp';
import useLoadSnowResults from './hooks/useLoadSnowResults';
import FindObject from './classes/FindObject';

const SOCKET_URL = 'ws://localhost:8080/snow-finder';

function App() {

    const { message, send } = useStomp(SOCKET_URL);
    const { citiesWithSnow, loadedNumber, toLoadNumber, city, otherMessage, reset } 
        = useLoadSnowResults({ message });

    const [zipCode, setZipCode] = useState(0);
    const [radius, setRadius] = useState(0);
    const [searchType, setSearchType] = useState('zipCode');

    const zipCodeRef = useRef(null);

    const handleCheck = (e: any) => {
        setSearchType(String(e.target.id));
    }

    const submit = () => {
        reset();
        if (searchType === 'zipCode') {
            send('/find/snowUsingZip', JSON.stringify(new FindObject('zip', zipCode, radius)))
        } else {
            send("/find/snowInUS", null);
        }
    }

    return (
        <div className={'body'}>
            <div className='blueSky'></div>
            <div className='inputContainer'>
                <h1>SnowFinder</h1>
                <div className='tabs d-inline'>
                <input 
                    className='tab invisible' 
                    type="radio" 
                    id="USCities" 
                    name="findType" 
                    value="snowInUS" 
                    checked={searchType === 'USCities'}
                />
                <label className='tabLabel' htmlFor="USCities">Major US Cities</label>
                <span>|</span>
                <input 
                    className='tab invisible' 
                    type="radio" 
                    id="zipCode" 
                    name="findType" 
                    value="snowUsingZip" 
                    ref={zipCodeRef}
                    checked={searchType === 'zipCode'}
                />
                <label className='tabLabel activeTab' htmlFor="zipCode">Zip Code Radius</label>
                </div>
                <label>
                    Search Location:
                </label>
                <input 
                    className='input'
                    type={'text'} 
                    placeholder={'Zip Code'}
                    value={zipCode}
                    onChange={(e) => setZipCode(Number(e.target.value))}
                />
                <input 
                    className='input'
                    type={'text'} 
                    placeholder={'Radius'}
                    value={radius}
                    onChange={(e) => setRadius(Number(e.target.value))}
                />
                <button onClick={submit}>Search</button>
                <div className='results'>
                    <div className='citiesWithSnow'>{citiesWithSnow}</div>
                    <span>&nbsp;cities with snow found (</span>
                    <div className='loaded'>{loadedNumber}</div>
                    <span>&nbsp;out of </span>
                    <div className='loaded'>{toLoadNumber}</div>
                    <span>)</span>

                    <div className='log'></div>
                </div>
            </div>
        </div>
    )

    // return (
    //     <div className="App">
    //         <header className="App-header">
    //             <img src={logo} className="App-logo" alt="logo" />
    //             <p>
    //                 {message}
    //             </p>
    //             <a
    //                 className="App-link"
    //                 onClick={() => send("/find/snowInUS")}
    //                 target="_blank"
    //                 rel="noopener noreferrer"
    //             >
    //                 Find snow in major US Cities
    //             </a>
    //             <a
    //                 className="App-link"
    //                 onClick={() => send('/find/snowUsingZip', JSON.stringify(new FindObject('zip', 74467, 50)))}
    //                 target="_blank"
    //                 rel="noopener noreferrer"
    //             >
    //                 Find snow 50 miles near 74467
    //             </a>
    //         </header>
    //     </div>
    // );
}

export default App;
