import { useEffect, useState } from "react";
import CityResponse from "../classes/CityResponse";

interface useLoadSnowResultsProps {
    message: string;
};

const cityPropMap: string[] = ['hasSnow', 'name', 'state', 'snowDepth', 'temp', 'distanceAway'];

export default function useLoadSnowResults (props: useLoadSnowResultsProps) {

    const { message } = props;

    const [citiesWithSnow, setCitiesWithSnow] = useState(0);
    const [loadedNumber, setLoadedNumber] = useState(0);
    const [toLoadNumber, setToLoadNumber] = useState(0);
    const [otherMessage, setOtherMessage] = useState('');
    const [city, setCity] = useState(new CityResponse());

    useEffect(() => {

        console.log(message);

        if (message[0] === 'X' || message[0] === 'O') {
            const m = message, cityProps = new CityResponse(
                m[0] === 'X', m[1], m[2], Number(m[3]), Number(m[4]), Number(m[5])
            );
            setCity(cityProps);

            if (cityProps.hasSnow) setCitiesWithSnow(prev => ++prev);
            setLoadedNumber(prev => ++prev);
        } else if (message.indexOf(' results') !== -1) {
            setToLoadNumber(Number(message.substring(0, message.indexOf(' '))));
        } else {
            setOtherMessage(message);
        }
    }, [message]);

    const reset = () => {
        setCitiesWithSnow(0);
        setLoadedNumber(0);
        setToLoadNumber(0);
        setOtherMessage('');
    }

    return {
        citiesWithSnow,
        loadedNumber,
        toLoadNumber,
        otherMessage,
        city,
        reset
    }
}
